    @Override
    public void paint(Graphics g) {
        super.paint(g);
        double total_marcas = (double) hp + lenovo + dell + acer + apple + toshiba + brother + samsung + asus + alienware + xerox;

        int grados_acer = (int) ((acer * 360) / total_marcas);
        int grados_hp = (int) ((hp * 360) / total_marcas);
        int grados_lenovo = (int) ((lenovo * 360) / total_marcas);
        int grados_dell = (int) ((dell * 360) / total_marcas);
        int grados_apple = (int) ((apple * 360) / total_marcas);
        int grados_toshiba = (int) ((toshiba * 360) / total_marcas);
        int grados_brother = (int) ((brother * 360) / total_marcas);
        int grados_samsung = (int) ((samsung * 360) / total_marcas);
        int grados_asus = (int) ((asus * 360) / total_marcas);
        int grados_alienware = (int) ((alienware * 360) / total_marcas);
        int grados_xerox = (int) ((xerox * 360) / total_marcas);

        //marca acer
        g.setColor(new Color(175, 122, 197));
        g.fillArc(25, 100, 270, 270, 0, grados_acer);
        g.fillRect(310, 120, 20, 20);
        g.drawString(acer + " de Acer", 340, 135);

        //marca alienware
        g.setColor(new Color(0, 255, 0));
        g.fillArc(25, 100, 270, 270, grados_acer, grados_alienware);
        g.fillRect(310, 150, 20, 20);
        g.drawString(alienware + " de Alienware", 340, 165);

        // HP
        g.setColor(new Color(255, 0, 0));
        g.fillArc(25, 100, 270, 270, grados_acer + grados_alienware, grados_hp);
        g.fillRect(310, 180, 20, 20);
        g.drawString(hp + " de HP", 340, 195);

        // Lenovo
        g.setColor(new Color(0, 0, 255));
        g.fillArc(25, 100, 270, 270, grados_acer + grados_alienware + grados_hp, grados_lenovo);
        g.fillRect(310, 210, 20, 20);
        g.drawString(lenovo + " de Lenovo", 340, 225);

        // Dell
        g.setColor(new Color(255, 255, 0));
        g.fillArc(25, 100, 270, 270, grados_acer + grados_alienware + grados_hp + grados_lenovo, grados_dell);
        g.fillRect(310, 240, 20, 20);
        g.drawString(dell + " de Dell", 340, 255);

        // Apple
        g.setColor(new Color(0, 255, 255));
        g.fillArc(25, 100, 270, 270, grados_acer + grados_alienware + grados_hp + grados_lenovo + grados_dell, grados_apple);
        g.fillRect(310, 270, 20, 20);
        g.drawString(apple + " de Apple", 340, 285);

        // Toshiba
        g.setColor(new Color(255, 0, 255));
        g.fillArc(25, 100, 270, 270, grados_acer + grados_alienware + grados_hp + grados_lenovo + grados_dell + grados_apple, grados_toshiba);
        g.fillRect(310, 300, 20, 20);
        g.drawString(toshiba + " de Toshiba", 340, 315);

        // Brother
        g.setColor(new Color(192, 192, 192));
        g.fillArc(25, 100, 270, 270, grados_acer + grados_alienware + grados_hp + grados_lenovo + grados_dell + grados_apple + grados_toshiba, grados_brother);
        g.fillRect(310, 330, 20, 20);
        g.drawString(brother + " de Brother", 340, 345);

        // Samsung
        g.setColor(new Color(128, 0, 0));
        g.fillArc(25, 100, 270, 270, grados_acer + grados_alienware + grados_hp + grados_lenovo + grados_dell + grados_apple + grados_toshiba + grados_brother, grados_samsung);
        g.fillRect(430, 120, 20, 20);
        g.drawString(samsung + " de Samsung", 460, 135);

        // Asus
        g.setColor(new Color(128, 128, 0));
        g.fillArc(25, 100, 270, 270, grados_acer + grados_alienware + grados_hp + grados_lenovo + grados_dell + grados_apple + grados_toshiba + grados_brother + grados_samsung, grados_asus);
        g.fillRect(430, 150, 20, 20);
        g.drawString(asus + " de Asus", 460, 165);

        // Xerox
        g.setColor(new Color(0, 128, 0));
        g.fillArc(25, 100, 270, 270, grados_acer + grados_alienware + grados_hp + grados_lenovo + grados_dell + grados_apple + grados_toshiba + grados_brother + grados_samsung + grados_asus, grados_xerox);
        g.fillRect(430, 180, 20, 20);
        g.drawString(xerox + " de Xerox", 460, 195);

    }
